CREATE OR REPLACE FUNCTION obter_atrib_regra ( nm_tabela_p text, nm_atributo_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


-----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
--		C U I D A D O    A O  A L T E R A R   E S T A  F U N C T I O N
-- SE  FOR  ADICIONAR  UM NOVO PARÂMETRO DE RETORNO DEVE-SE SINCRONIZAR A ALTERAÇÂO COM O JAVA
-- O DBPanel do JAVA CONTEM UM TRATAMENTO FIXO PELO NÙMERO DE PARÂMETROS DE RETORNO PARA GARANTIR O FUNCIONAMENTO
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
ds_retorno_w		varchar(255);
ie_tipo_regra_w		varchar(1);
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;
cd_funcao_W		bigint := wheb_usuario_pck.get_cd_funcao;

C01 CURSOR FOR
	SELECT	vl_minimo               || ';' ||
		vl_maximo               || ';' ||
		vl_minimo_permitido     || ';' ||
		vl_maximo_permitido     || ';' ||
		ds_msg_aviso            || ';' ||
		ds_msg_restricao	|| ';' ||
		ie_tipo_calculo		|| ';' ||
		ds_cor_label
	from   tabela_atrib_regra
	where  nm_tabela       = nm_tabela_p
	and    nm_atributo	= nm_atributo_p
	and	coalesce(cd_perfil, coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
	and 	coalesce(nm_usuario_param, wheb_usuario_pck.get_nm_usuario) 	= wheb_usuario_pck.get_nm_usuario
	and 	coalesce(cd_funcao, cd_funcao_w)	= cd_funcao_w
	and   coalesce(cd_estabelecimento, cd_estabelecimento_w)	= cd_estabelecimento_w
	ORDER BY	coalesce(nm_usuario_param,'AAA'),
			coalesce(cd_perfil,0),
			coalesce(cd_estabelecimento,0),
			coalesce(cd_funcao,0);


BEGIN

	OPEN C01;
	LOOP
		FETCH C01 INTO
			ds_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */

	END LOOP;
	CLOSE C01;

return     ds_retorno_w;

end;
$body$
