SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_atrib_unid_med_unica ( nm_tabela_p text, nm_atributo_p text, dt_referencia_p timestamp, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w  bigint;
IE_UNIDADE_MEDIDA_w varchar(15);
nr_seq_unid_med_w bigint;

ds_resultado_w varchar(60);


BEGIN

ds_resultado_w := '';

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	tab_atrib_regra_unid_med
where	nm_tabela = nm_tabela_p
  and	nm_atributo = nm_atributo_p
  and	dt_referencia_p between dt_inicio_vigencia and fim_dia(coalesce(dt_final_vigencia,dt_referencia_p));

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') and (ie_opcao_p <> 0) then
	select	IE_UNIDADE_MEDIDA,
		nr_seq_unid_med
	into STRICT	IE_UNIDADE_MEDIDA_w,
		nr_seq_unid_med_w
	from tab_atrib_regra_unid_med
	where nr_sequencia = nr_sequencia_w;

	if (IE_UNIDADE_MEDIDA_w = 'U') and (nr_seq_unid_med_w IS NOT NULL AND nr_seq_unid_med_w::text <> '') then
		select	substr(EHR_Obter_Desc_Unid_Med(nr_seq_unid_med, 'C'),1,60)
		into STRICT	ds_resultado_w
		from	tab_atrib_regra_item
		where nr_sequencia = nr_seq_unid_med_w;
	end if;
end if;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
