CREATE OR REPLACE FUNCTION obter_atualiza_escala_indice ( nr_sequencia_p bigint, cd_perfil_p bigint, ie_escala_p bigint) RETURNS varchar AS $body$
DECLARE


ie_atualizar_w	varchar(1) := 'S';


BEGIN

select	coalesce(max(ie_atualizar),'S')
into STRICT	ie_atualizar_w
from	perfil_escala_indice b,
	grupo_escala_indice_item a
where	a.nr_seq_grupo	= b.nr_seq_grupo
and	b.nr_sequencia	= nr_sequencia_p
and	a.ie_escala	= ie_escala_p
and	b.cd_perfil	= cd_perfil_p;

return	ie_atualizar_w;

end;
$body$
LANGUAGE PLPGSQL
