CREATE OR REPLACE FUNCTION obter_atv_pj (cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);


BEGIN

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	select  max(substr(obter_desc_cnae(a.nr_sequencia),1,80))
	into STRICT 	ds_retorno_w
	from    pls_cnae a,
		pessoa_juridica b
	where 	b.nr_seq_cnae = a.nr_sequencia
	and	b.cd_cgc = cd_cgc_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
