CREATE OR REPLACE FUNCTION obter_audit_mat_valor (nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_unitario_w		double precision;
vl_total_w		double precision;


BEGIN
select	coalesce(vl_unitario,0),
	coalesce(vl_material,0)
into STRICT	vl_unitario_w,
	vl_total_w
from	material_atend_paciente
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'U') then
	RETURN vl_unitario_w;
elsif (ie_opcao_p = 'T') then
	RETURN vl_total_w;
end if;

END;
$body$
LANGUAGE PLPGSQL
