CREATE OR REPLACE FUNCTION obter_autorizacao_agenda_cir (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_autorizacao_w	varchar(255);


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	select	substr(max(obter_valor_dominio(1227,ie_autorizacao)),1,255)
	into STRICT	ds_autorizacao_w
	from	agenda_paciente
	where	nr_sequencia	= nr_seq_agenda_p;
end if;

return	ds_autorizacao_w;

end;
