CREATE OR REPLACE FUNCTION obter_autorizacao_pend_dup ( nr_seq_autorizacao_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pendencia_duplicada_w	varchar(1) := 'N';
nr_seq_paciente_setor_w		bigint;
nr_ciclo_w			bigint;


BEGIN

select	nr_seq_paciente_setor,
	nr_ciclo
into STRICT	nr_seq_paciente_setor_w,
	nr_ciclo_w
from	autorizacao_convenio
where	nr_sequencia = nr_seq_autorizacao_convenio_p;


select	coalesce(max(q.ie_pendencia_duplicada),'N')
into STRICT	ie_pendencia_duplicada_w
from	paciente_atendimento p,
	qt_pendencia_agenda q
where	p.nr_seq_pend_agenda 	=  q.nr_sequencia
and	p.nr_ciclo 		= nr_ciclo_w
and	p.nr_seq_paciente 	= nr_seq_paciente_setor_w;


return	ie_pendencia_duplicada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
