CREATE OR REPLACE FUNCTION obter_autorizacoes_conta ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
nr_seq_autorizacao_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from (
		SELECT  a.nr_sequencia
		from	autorizacao_convenio a,
			procedimento_paciente x,
			procedimento_autorizado y
		where	x.nr_seq_proc_autor = y.nr_sequencia
		and     y.nr_sequencia_autor = a.nr_sequencia
		and	x.nr_interno_conta = nr_interno_conta_p
		
union

		select	a.nr_sequencia
		from	autorizacao_convenio a,
			material_atend_paciente x,
			material_autorizado y
		where	x.nr_seq_mat_autor = y.nr_sequencia
		and	y.nr_sequencia_autor = a.nr_sequencia
		and	x.nr_interno_conta = nr_interno_conta_p
		) alias0
	where	1 = 1
	group by	nr_sequencia
	order by	nr_sequencia;


BEGIN

if (coalesce(nr_interno_conta_p,0) <> 0) then

	open C01;
	loop
	fetch C01 into
		nr_seq_autorizacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (length(ds_retorno_w || nr_seq_autorizacao_w || ',') <= 4000) then
			ds_retorno_w	:= ds_retorno_w || nr_seq_autorizacao_w || ',';
		end if;

		end;
	end loop;
	close C01;

end if;

ds_retorno_w	:= substr(ds_retorno_w, 1, length(ds_retorno_w) - 1);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
