SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_autoriz_estagio_concat ( nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_tipo_w	varchar(255);
ds_estagio_w	varchar(255);
ds_retorno_w	varchar(2000);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(299368, null, wheb_usuario_pck.get_nr_seq_idioma);--Tipo
expressao2_w	varchar(255) := obter_desc_expressao_idioma(289547, null, wheb_usuario_pck.get_nr_seq_idioma);--EstÃ¡gio
C01 CURSOR FOR
SELECT 	substr(obter_valor_dominio(1377, ie_tipo_autorizacao),1,254),
	substr(obter_descricao_padrao('ESTAGIO_AUTORIZACAO', 'DS_ESTAGIO', nr_seq_estagio),1,254)
from 	autorizacao_convenio
where 	nr_seq_agenda 	= nr_seq_agenda_p
order by dt_atualizacao;


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then

	open C01;
	loop
	fetch C01 into
		ds_tipo_w,
		ds_estagio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (ds_tipo_w IS NOT NULL AND ds_tipo_w::text <> '') and (ds_estagio_w IS NOT NULL AND ds_estagio_w::text <> '') then

				if (coalesce(ds_retorno_w::text, '') = '') then
					ds_retorno_w 	:= 	substr(expressao1_w || ': ' || ds_tipo_w || ' - '||expressao2_w||': ' || ds_estagio_w,1,500);
				else
					ds_retorno_w 	:= 	substr(ds_retorno_w ||' / '|| expressao1_w || ': ' || ds_tipo_w || ' - '||expressao2_w||': ' || ds_estagio_w,1,1000);
				end if;

		end if;

		end;
	end loop;
	close C01;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_autoriz_estagio_concat ( nr_seq_agenda_p bigint) FROM PUBLIC;

