CREATE OR REPLACE FUNCTION obter_autor_quimio ( nr_seq_atendimento_p bigint, nr_ciclo_p bigint, ds_dia_ciclo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*  ****************** CUIDADO AO ALTERAR OBJETO, FAVOR ENTRAR EM CONTATO COM O GRUPO  RESPONSÁVEL PELA QUIMIOTERAPIA ANTES DE REALIZAR ALGUMA ALTERAÇÃO ****************
ie_opcao_p
	a - autorização
	s - status da autorização
	N - estagio da autorização
*/
nr_seq_autorizacao_w	bigint;
ds_estagio_w		varchar(255);
ds_retorno_w		varchar(255);
nr_seq_estagio_w	bigint;
nr_seq_paciente_w	bigint;


BEGIN


select 	max(nr_seq_paciente)
into STRICT	nr_seq_paciente_w
from 	paciente_atendimento
where 	nr_seq_atendimento = nr_seq_atendimento_p;

select	max(nr_sequencia)
into STRICT	nr_seq_autorizacao_w
from	autorizacao_convenio
where	nr_seq_paciente = nr_seq_atendimento_p;


if (coalesce(nr_seq_autorizacao_w::text, '') = '') then
	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_autorizacao_w
	from	autorizacao_convenio
	where	nr_seq_paciente_setor = nr_seq_paciente_w
	and	nr_ciclo = nr_ciclo_p;
	end;
end if;

if (nr_seq_autorizacao_w > 0) then

	select	substr(obter_descricao_padrao('ESTAGIO_AUTORIZACAO', 'DS_ESTAGIO', nr_seq_estagio),1,254),
		nr_seq_estagio
	into STRICT	ds_estagio_w,
		nr_seq_estagio_w
	from	autorizacao_convenio
	where	nr_sequencia = nr_seq_autorizacao_w;
end if;

if (ie_opcao_p = 'A') then
	ds_retorno_w	:= nr_seq_autorizacao_w;
elsif (ie_opcao_p = 'S') then
	ds_retorno_w	:= ds_estagio_w;
elsif (ie_opcao_p = 'N') then
	ds_retorno_w	:= nr_seq_estagio_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
