CREATE OR REPLACE FUNCTION obter_auxiliar_cirurgia (nr_cirurgia_p bigint, ie_funcao_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nm_participante_w	varchar(80);
cd_pessoa_fisica_w	varchar(10);
ds_retorno_w		varchar(50);

/*
ie_opcao_p
 N - Nome auxiliar
 C - CÃ³digo
*/
BEGIN

select	max(nm_participante),
	max(cd_pessoa_fisica)
into STRICT	nm_participante_w,
	cd_pessoa_fisica_w
from   	cirurgia_participante
where  	nr_cirurgia	= nr_cirurgia_p
and	ie_funcao   	= ie_funcao_p;


if (ie_opcao_p = 'N') then
	ds_retorno_w	:= nm_participante_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_pessoa_fisica_w;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
