CREATE OR REPLACE FUNCTION obter_avaliacao_medic ( cd_material_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_tipo_aval_w		bigint := 0;
cd_classe_material_w	integer;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w	smallint;
cd_convenio_w	        integer;



BEGIN
if (coalesce(cd_material_p,0) > 0) then
	select	obter_estrutura_material(cd_material_p,'C'),
			obter_estrutura_material(cd_material_p,'G'),
			obter_estrutura_material(cd_material_p,'S')
	into STRICT		cd_classe_material_w,
			cd_grupo_material_w,
			cd_subgrupo_material_w
	;

	cd_convenio_w := Obter_Convenio_Atendimento(nr_atendimento_p);

	select	coalesce(max(nr_seq_tipo_aval),0)
	into STRICT		nr_seq_tipo_aval_w
	from		rep_avaliacao_medicamento
	where	coalesce(cd_material,cd_material_p) = cd_material_p
	and		coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
	and		coalesce(cd_convenio,cd_convenio_w) = cd_convenio_w
	and		coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w
	and		coalesce(cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w;

end if;

return nr_seq_tipo_aval_w;

end 	;
$body$
