CREATE OR REPLACE FUNCTION obter_aviso_agenda (cd_agenda_p bigint, dt_aviso_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_aviso_w	varchar(2000);


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_aviso_p IS NOT NULL AND dt_aviso_p::text <> '') then
	begin
		select	ds_aviso
		into STRICT	ds_aviso_w
		from	agenda_aviso
		where	cd_agenda 	= cd_agenda_p
		and	dt_aviso	= trunc(dt_aviso_p);
	exception
	when others then
		null;
	end;
end if;

return ds_aviso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
