CREATE OR REPLACE FUNCTION obter_aviso_horario_agenda ( cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2000);
ie_dia_semana_w			varchar(2);


BEGIN

if (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	ie_dia_semana_w := Obter_Cod_Dia_Semana(dt_agenda_p);
	select	max(substr(ds_aviso,1,2000))
	into STRICT	ds_retorno_w
	from	agenda_horario_aviso
	where	nr_sequencia =  (SELECT max(x.nr_sequencia)
				from   	agenda_horario_aviso x
				where	dt_agenda_p >= x.dt_inicial
				and 	dt_agenda_p <  x.dt_final
				and	x.cd_agenda =  cd_agenda_p
				and 	((x.ie_dia_semana  = ie_dia_semana_w) or (coalesce(x.ie_dia_semana::text, '') = '') or ((x.ie_dia_semana = 9) and (ie_dia_semana_w not in (7,1))))
				and 	((coalesce(ie_considera_entre_hor,'N') <> 'S') or ((coalesce(ie_considera_entre_hor,'N') = 'S') and (to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(dt_agenda_p, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') >=
					to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(dt_inicial, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')) and (to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(dt_agenda_p, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') <
					to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(dt_final, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')))));

end if;

return ds_retorno_w;

end;
$body$
