SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_aviso_pendente (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(500) := '';
cd_medico_w		varchar(10);
nm_medico_w		varchar(100);
ie_sexo_w		varchar(1);


BEGIN 
select	coalesce(max(a.cd_medico_atual),0) 
into STRICT	cd_medico_w 
from	atendimento_paciente b, 
	atendimento_troca_medico a 
where	a.nr_atendimento	= b.nr_atendimento 
and	a.cd_medico_atual	= b.cd_medico_resp 
and	a.nr_sequencia		= (	SELECT	coalesce(max(c.nr_sequencia),0) 
					from	atendimento_troca_medico c 
					where	c.nr_atendimento = nr_atendimento_p 
					and	coalesce(c.dt_ciente::text, '') = '');
 
if (cd_medico_w <> 0) then 
 
	select	ie_sexo, 
			substr(obter_nome_pf(cd_pessoa_fisica),1,100) 
	into STRICT	ie_sexo_w, 
			nm_medico_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica = cd_medico_w;
		 
	 
	if (ie_sexo_w = 'M') then 
		ds_retorno_w := substr(wheb_mensagem_pck.get_texto(327729,'NM_MEDICO=' || nm_medico_w),1,4000);	
	elsif (ie_sexo_w = 'F') then 
		ds_retorno_w := substr(wheb_mensagem_pck.get_texto(327730,'NM_MEDICO=' || nm_medico_w),1,4000);	
	end if;
end if;
 
RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_aviso_pendente (nr_atendimento_p bigint) FROM PUBLIC;

