CREATE OR REPLACE FUNCTION obter_bairro_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_bairro_w			varchar(40);

c01 CURSOR FOR
	SELECT	substr(ds_bairro,1,40)
	FROM	compl_pessoa_fisica
	WHERE	cd_pessoa_fisica = cd_pessoa_fisica_p
	AND	(ds_bairro IS NOT NULL AND ds_bairro::text <> '')
	ORDER BY ie_tipo_complemento DESC,
		ds_bairro DESC;


BEGIN
ds_bairro_w	:= wheb_mensagem_pck.get_texto(305494); --'NÃ£o Informado';
OPEN C01;
LOOP
FETCH C01 INTO
	ds_bairro_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_bairro_w	:= ds_bairro_w;
END LOOP;
CLOSE C01;

RETURN ds_bairro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
