SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_baixa_conrece (nr_sequencia_p bigint, ie_considera_adic_p text default 'S') RETURNS bigint AS $body$
DECLARE


vl_vinculado_w		double precision;
vl_estorno_w		double precision;
vl_adicional_w		double precision;
cd_estabelecimento_w	smallint;
ie_vl_conv_rec_adic_w	varchar(1);
nr_lote_contabil_rec_w	bigint;
vl_vinc_lote_audit_w	double precision;
ie_deduzir_adic_w	varchar(1);
ie_soma_vl_adic_w	varchar(1) := 'N';
cd_convenio_w		bigint;
dt_recebimento_w	timestamp;
ie_passivo_saldo_tit_w	parametro_contas_receber.ie_passivo_saldo_tit%type;
vl_recebido_bordero_w	convenio_receb_titulo.vl_recebido%type;


BEGIN

select	coalesce(sum(vl_vinculacao),0)
into STRICT 	vl_vinculado_w
from 	convenio_ret_receb
where 	nr_seq_receb = nr_sequencia_p;

select	coalesce(sum(vl_vinculado),0)
into STRICT 	vl_vinc_lote_audit_w
from 	lote_audit_hist_receb
where 	nr_seq_receb = nr_sequencia_p;

select	max(cd_estabelecimento),
	max(ie_deduzir_adic),
	max(cd_convenio),
	max(dt_recebimento)
into STRICT	cd_estabelecimento_w,
	ie_deduzir_adic_w,
	cd_convenio_w,
	dt_recebimento_w
from	convenio_receb
where 	nr_sequencia = nr_sequencia_p;

select 	coalesce(max(ie_vl_conv_rec_adic),'N'),
	coalesce(max(ie_passivo_saldo_tit),'S')
into STRICT	ie_vl_conv_rec_adic_w,
	ie_passivo_saldo_tit_w
from 	parametro_contas_receber
where 	cd_estabelecimento = cd_estabelecimento_w;

ie_vl_conv_rec_adic_w	:= coalesce(OBTER_SE_ATUALIZA_JUROS_RECEB(cd_convenio_w,dt_recebimento_w), ie_vl_conv_rec_adic_w);

select 	coalesce(sum(vl_estorno + coalesce(vl_adic_estorno,0)),0)
into STRICT 	vl_estorno_w
from 	convenio_receb_estorno
where 	nr_seq_receb = nr_sequencia_p;

vl_adicional_w := 0;

if (ie_considera_adic_p = 'S')  then
	select	coalesce(sum(coalesce(a.vl_adicional,0) + coalesce(a.vl_cambial_ativo,0) - CASE WHEN ie_passivo_saldo_tit_w='N' THEN 0  ELSE coalesce(a.vl_cambial_passivo,0) END ),0)
	into STRICT	vl_adicional_w
	from	convenio_receb_adic a,
		transacao_financeira b
	where	a.nr_seq_receb = nr_sequencia_p
	and	a.nr_seq_trans_financ = b.nr_sequencia
	and (ie_vl_conv_rec_adic_w = 'S' or (ie_vl_conv_rec_adic_w = 'O' and coalesce(ie_deduzir_adic_w,'S') = 'S'));
end if;
	
ie_soma_vl_adic_w := obter_param_usuario(27, 212, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w, ie_soma_vl_adic_w);	

if (coalesce(ie_soma_vl_adic_w,'N') = 'S') and (coalesce(vl_adicional_w,0) > 0) then
		
	vl_adicional_w := vl_adicional_w * -1;	
end if;

/*OS 2041319 - Considerar tambem os recebimentos de convenio vinculados ao titulo no bordero*/

select	coalesce(sum(a.vl_recebido),0)
into STRICT	vl_recebido_bordero_w
from	convenio_receb_titulo a
where	a.nr_seq_receb = nr_sequencia_p
and	(a.nr_bordero IS NOT NULL AND a.nr_bordero::text <> ''); --Somente deduzir itens desta tabela que possuem nr_bordero informado, senao vai deduzir indevidamente os titulos que constam nesta tabela mas que foram incluidos no processo normal quando baixa o retorno convenio
return vl_vinculado_w + vl_vinc_lote_audit_w + vl_estorno_w + vl_adicional_w + vl_recebido_bordero_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_baixa_conrece (nr_sequencia_p bigint, ie_considera_adic_p text default 'S') FROM PUBLIC;

