SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_balanco_hidrico_diario (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_acumulado_w			double precision;
cd_estabelecimento_w		smallint;
ie_liberar_ganho_perda_w	varchar(1);
dt_inicio_w					timestamp;
dt_fim_w					timestamp;


BEGIN

dt_inicio_w	:= trunc(clock_timestamp());
dt_fim_w	:= fim_dia(dt_inicio_w);

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;

select	coalesce(max(ie_liberar_ganho_perda),'N')
into STRICT	ie_liberar_ganho_perda_w
from	parametro_medico
where	cd_estabelecimento	= cd_estabelecimento_w;
	
if (ie_liberar_ganho_perda_w	= 'N') then
	
	select	coalesce(sum(CASE WHEN c.ie_perda_ganho='G' THEN a.qt_volume  ELSE 0 END ) - sum(CASE WHEN c.ie_perda_ganho='P' THEN a.qt_volume  ELSE 0 END ),0) qt_saldo
	into STRICT	qt_acumulado_w
	from	grupo_perda_ganho c,
			tipo_perda_ganho b,
			atendimento_perda_ganho a
	where	a.nr_seq_tipo	= b.nr_sequencia
	and		b.nr_seq_grupo	= c.nr_sequencia
	and		coalesce(b.ie_soma_bh,'S')	= 'S'
	and		a.nr_atendimento = nr_atendimento_p
	and     coalesce(a.ie_situacao,'A') = 'A'
	and		a.DT_MEDIDA between dt_inicio_w and  dt_fim_w;
	
else
	select	coalesce(sum(CASE WHEN c.ie_perda_ganho='G' THEN a.qt_volume  ELSE 0 END ) - sum(CASE WHEN c.ie_perda_ganho='P' THEN a.qt_volume  ELSE 0 END ),0) qt_saldo
	into STRICT	qt_acumulado_w
	from	grupo_perda_ganho c,
			tipo_perda_ganho b,
			atendimento_perda_ganho a
	where	a.nr_seq_tipo	= b.nr_sequencia
	and		b.nr_seq_grupo	= c.nr_sequencia
	and		coalesce(b.ie_soma_bh,'S')	= 'S'
	and		a.nr_atendimento = nr_atendimento_p
	and		coalesce(a.dt_referencia, a.DT_MEDIDA) between dt_inicio_w and  dt_fim_w
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and     coalesce(a.ie_situacao,'A') = 'A';
end if;		



Return qt_acumulado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
