CREATE OR REPLACE FUNCTION obter_banco_ativo (cd_banco_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

SELECT 	CASE WHEN coalesce(a.ie_situacao,'I')='I' THEN 'N'  ELSE 'S' END
into STRICT 	ds_retorno_w
FROM 	banco a
WHERE 	a.cd_banco = cd_banco_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
