CREATE OR REPLACE FUNCTION obter_banco_e_conta (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_conta_w	varchar(254);


BEGIN

select	ds_banco || ': ' || cd_conta || CASE WHEN coalesce(ie_digito_conta, '-1')='-1' THEN ''  ELSE '-' END  || ie_digito_conta
into STRICT	ds_conta_w
from	banco_estabelecimento_v
where	nr_sequencia = nr_sequencia_p;

return	ds_conta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
