CREATE OR REPLACE FUNCTION obter_banco_padrao_pag ( cd_cgc_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
Opções:
B: Banco
A: Agencia
C: Conta
CB: Código do Banco
*/
ds_retorno_w		varchar(255);
cd_banco_w		varchar(255);
cd_agencia_w		varchar(255);
cd_digito_agencia_w	varchar(255);
cd_conta_w		varchar(255);
cd_digito_conta_w		varchar(255);


BEGIN

select	cd_banco,
	cd_agencia_bancaria,
	ie_digito_agencia,
	nr_conta,
	nr_digito_conta
into STRICT	cd_banco_w,
	cd_agencia_w,
	cd_digito_agencia_w,
	cd_conta_w,
	cd_digito_conta_w
from	pessoa_juridica_conta
where	cd_cgc = cd_cgc_p
and	ie_conta_pagamento = 'S';

if (ie_opcao_p = 'B') then
	ds_retorno_w := obter_nome_banco(cd_banco_w);

elsif (ie_opcao_p = 'CB') then
	ds_retorno_w := cd_banco_w;

elsif (ie_opcao_p = 'A') then

	if (cd_digito_agencia_w IS NOT NULL AND cd_digito_agencia_w::text <> '') then
		ds_retorno_w := (cd_agencia_w||'-'|| cd_digito_agencia_w);
	else
		ds_retorno_w := cd_agencia_w;
	end if;

elsif (ie_opcao_p = 'C') then

	if (cd_digito_conta_w IS NOT NULL AND cd_digito_conta_w::text <> '') then
		ds_retorno_w := (cd_conta_w||'-'||cd_digito_conta_w);
	else
		ds_retorno_w := cd_conta_w;
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
