CREATE OR REPLACE FUNCTION obter_barras_material (cd_material_p bigint, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(4000);
cd_barra_material_w 	varchar(255);

c01 CURSOR FOR
SELECT	a.cd_barra_material
from	MATERIAL_COD_BARRA a
where	a.cd_material = cd_material_p
and	((a.cd_cgc_fabricante = cd_cgc_p) or (coalesce(cd_cgc_p,'0') = '0'));


BEGIN
open C01;
loop
fetch C01 into
	cd_barra_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (length(ds_retorno_w || cd_barra_material_w || ', ') < 4000) then
		ds_retorno_w := ds_retorno_w || cd_barra_material_w || ', ';
	end if;
	end;
end loop;
close C01;

ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w)-2);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
