CREATE OR REPLACE FUNCTION obter_base_calc_tit_bordero ( nr_bordero_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_base_calculo_w	double precision;
vl_base_calculo_tot_w	double precision;

C01 CURSOR FOR 
SELECT	max(vl_base_calculo) 
from	titulo_pagar_imposto b, 
	titulo_pagar_bordero_v a 
where	a.nr_titulo = b.nr_titulo 
and	a.nr_bordero = nr_bordero_p 
group by a.nr_titulo;


BEGIN 
 
if (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') then 
	open C01;
	loop 
	fetch C01 into 
		vl_base_calculo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		vl_base_calculo_tot_w := coalesce(vl_base_calculo_tot_w,0) + coalesce(vl_base_calculo_w,0);
		end;
	end loop;
	close C01;
end if;
 
return	vl_base_calculo_tot_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_base_calc_tit_bordero ( nr_bordero_p bigint) FROM PUBLIC;

