CREATE OR REPLACE FUNCTION obter_base_icmsst_nf ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


retorno_w		double precision;
icms_w		double precision;


BEGIN

select	coalesce(sum(a.vl_base_calculo),0)
into STRICT	icms_w
from	nota_fiscal_item_trib a,
	tributo b
where	a.cd_tributo 		= b.cd_tributo
and	a.nr_sequencia		= nr_sequencia_p
and	b.ie_tipo_tributo = 'ICMSST';

retorno_w	:= icms_w;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
