CREATE OR REPLACE FUNCTION obter_batch_hold ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_manual_hold_w	varchar(1);


BEGIN

ie_manual_hold_w := 'N';

select	coalesce(max(nr_sequencia), 0)
into STRICT	nr_sequencia_w
from	ap_lote_bloqueio
where	nr_seq_lote	= nr_seq_lote_p;

if (nr_sequencia_w > 0) then
	select	coalesce(IE_MANUAL_HOLD_BY_USER,'N')
	into STRICT 	ie_manual_hold_w
	from	ap_lote_bloqueio
	where	nr_sequencia	= nr_sequencia_w;

end if;

return ie_manual_hold_w;

end; /* GOLDENGATE_DDL_REPLICATION */
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
