SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_beneficiario_nf ( nr_sequencia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	0 - Pessoa fisica se existir
	1 - Pessoa juridica
	2 - Codigo do beneficiario (cd_pessoa_fisica ou cd_cnpj)
	
	*/
cd_pessoa_fisica_w	varchar(10);
cd_cgc_emitente_w	varchar(14);
cd_cgc_w		varchar(14);
ie_entrada_saida_w	varchar(1);
ds_beneficiario_w	varchar(255);


BEGIN

select	cd_pessoa_fisica,
	cd_cgc_emitente,
	cd_cgc,
	substr(obter_se_nota_entrada_saida(nr_sequencia),1,10)
into STRICT	cd_pessoa_fisica_w,
	cd_cgc_emitente_w,
	cd_cgc_w,
	ie_entrada_saida_w
from 	nota_fiscal
where 	nr_sequencia = nr_sequencia_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	select	CASE WHEN ie_opcao_p=2 THEN  cd_pessoa_fisica  ELSE nm_pessoa_fisica END
	into STRICT	ds_beneficiario_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;
end if;

if (ie_entrada_saida_w = 'E') then
	if (coalesce(cd_pessoa_fisica_w::text, '') = '') then
		select	CASE WHEN ie_opcao_p=2 THEN  cd_cgc  ELSE ds_razao_social END
		into STRICT	ds_beneficiario_w
		from	pessoa_juridica
		where	cd_cgc = cd_cgc_emitente_w;
	end if;
else
	if (coalesce(cd_pessoa_fisica_w::text, '') = '') then
		select	CASE WHEN ie_opcao_p=2 THEN  cd_cgc  ELSE ds_razao_social END
		into STRICT	ds_beneficiario_w
		from	pessoa_juridica
		where	cd_cgc = cd_cgc_w;
	end if;
end if;

return ds_beneficiario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
