CREATE OR REPLACE FUNCTION obter_benef_contrato (nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE

 
			 
ds_retorno_w		varchar(255);
			

BEGIN 
 
if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then 
	select distinct(obter_nome_pf_pj(max(b.cd_pessoa_fisica),null)) 
	into STRICT	ds_retorno_w 
	from	pls_contrato a, 
		pls_segurado b 
	where 	b.nr_seq_contrato = a.nr_sequencia 
	and	coalesce(b.nr_seq_titular::text, '') = '' 
	and	a.nr_sequencia 	 = nr_seq_contrato_p;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
