CREATE OR REPLACE FUNCTION obter_benef_valor_cpa (nr_bordero_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(400);			
ds_nome_pessoa_w	varchar(255);
vl_titulo_w		double precision;

C01 CURSOR FOR 
SELECT	ds_pessoa, 
	vl_titulo 
from (SELECT	obter_nome_pf_pj(b.cd_pessoa_fisica, b.cd_cgc) ds_pessoa, 
		(obter_dados_tit_pagar(b.nr_titulo, 'VST'))::numeric  vl_titulo 
	from	bordero_tit_pagar a, 
		titulo_pagar b 
	where	a.nr_titulo	= b.nr_titulo 
	and	a.nr_bordero	= nr_bordero_p 
	
union
 
	select	obter_nome_pf_pj(a.cd_pessoa_fisica, a.cd_cgc) ds_pessoa, 
		a.vl_liquido_bordero 
	from	titulo_pagar_v a 
	where	a.nr_bordero	= nr_bordero_p) alias4 ORDER by ds_pessoa LIMIT 5;
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	ds_nome_pessoa_w, 
	vl_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 ds_retorno_w := ds_retorno_w || rpad(substr(ds_nome_pessoa_w,1,40), 45, ' ') || lpad(Campo_Mascara_virgula(vl_titulo_w), 10, ' ') || ' ';
	end;
end loop;
close C01;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
