CREATE OR REPLACE FUNCTION obter_biometria_pessoa_fisica (cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
