CREATE OR REPLACE FUNCTION obter_biometria (cd_pessoa_fisica_p text, cd_empresa_biometria_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_biometria_w	varchar(2000);

ds_retorno_w	varchar(2000) := '';

c01 CURSOR FOR
	SELECT	nr_sequencia, ds_biometria
	from	pessoa_fisica_biometria
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	  and	cd_empresa_biometria	= cd_empresa_biometria_p;


BEGIN


open c01;
loop
	fetch c01 into	nr_sequencia_w,
			ds_biometria_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_retorno_w := ds_retorno_w || nr_sequencia_w || ',' || ds_biometria_w || ';';
end loop;
close c01;

return substr(ds_retorno_w, 1, length(ds_retorno_w) - 1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
