CREATE OR REPLACE FUNCTION obter_bioq_aprov_exam_lab (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


cd_medico_w	varchar(10);


BEGIN

if (nr_prescricao_p > 0) and (nr_seq_prescr_p > 0) then

	begin

	select	coalesce(max(b.cd_medico_resp),0)
	into STRICT	cd_medico_w
	from	exame_lab_resultado a,
			exame_lab_result_item b
	where	a.nr_seq_resultado	= b.nr_seq_resultado
	and		nr_prescricao = nr_prescricao_p
	and		nr_seq_prescr = nr_seq_prescr_p
	and		(b.nr_seq_material IS NOT NULL AND b.nr_seq_material::text <> '')
	and		(b.nr_seq_formato IS NOT NULL AND b.nr_seq_formato::text <> '');

	end;

end if;

return cd_medico_w;

end;
