CREATE OR REPLACE FUNCTION obter_bloc_canc_proj_rec ( nr_seq_proj_rec_p bigint) RETURNS bigint AS $body$
DECLARE


ie_status_w  bigint;


BEGIN

    select 	count(*)
    into STRICT 	ie_status_w
    from    projeto_recurso
    where	nr_sequencia = nr_seq_proj_rec_p
	and ((dt_bloqueio IS NOT NULL AND dt_bloqueio::text <> '') or (dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> ''));

	return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
