CREATE OR REPLACE FUNCTION obter_bloq_repasse_amaior ( cd_procedimento_p bigint, cd_material_p bigint, nr_seq_terceiro_p bigint, dt_repasse_p timestamp) RETURNS bigint AS $body$
DECLARE


regra_bloq_rep_amaior_w	bigint;
qt_regra_bloqueio_w	bigint;


BEGIN

select count(*)
into STRICT qt_regra_bloqueio_w
from regra_bloq_rep_amaior;

if (qt_regra_bloqueio_w > 0) then

	if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then

		select max(a.nr_sequencia)
		into STRICT regra_bloq_rep_amaior_w
		from regra_bloq_rep_amaior a
		where trunc(dt_repasse_p, 'dd') >= dt_inicio_vigencia
		and trunc(dt_repasse_p, 'dd') <= dt_fim_vigencia
		and (coalesce(a.cd_procedimento::text, '') = '' OR a.cd_procedimento = cd_procedimento_p)
		and (not exists (SELECT 1 from regra_bloq_terc_amaior b where b.nr_seq_regra_bloc = a.nr_sequencia)
			or exists (select 1 from regra_bloq_terc_amaior b where b.nr_seq_regra_bloc = a.nr_sequencia and b.nr_seq_terceiro = nr_seq_terceiro_p));

	elsif (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

		select max(a.nr_sequencia)
		into STRICT regra_bloq_rep_amaior_w
		from regra_bloq_rep_amaior a
		where trunc(dt_repasse_p, 'dd') >= dt_inicio_vigencia
		and trunc(dt_repasse_p, 'dd') <= dt_fim_vigencia
		and (coalesce(a.cd_material::text, '') = '' OR a.cd_material = cd_material_p)
		and (not exists (SELECT 1 from regra_bloq_terc_amaior b where b.nr_seq_regra_bloc = a.nr_sequencia)
			or exists (select 1 from regra_bloq_terc_amaior b where b.nr_seq_regra_bloc = a.nr_sequencia and b.nr_seq_terceiro = nr_seq_terceiro_p));

	end if;

end if;

return regra_bloq_rep_amaior_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
