CREATE OR REPLACE FUNCTION obter_bras_inc_exc_edicao ( cd_medicamento_p text, cd_apresentacao_p text, cd_laboratorio_p text) RETURNS varchar AS $body$
DECLARE


ie_situacao_w		varchar(1);


BEGIN

select 	coalesce(max(ie_situacao),'A')
into STRICT	ie_situacao_w
from 	brasindice_inc_exc_edicao a
where   a.cd_laboratorio 	= cd_laboratorio_p
and     a.cd_apresentacao	= cd_apresentacao_p
and    	a.cd_medicamento	= cd_medicamento_p
and	a.dt_atualizacao	= (	SELECT	max(x.dt_atualizacao)
					from	brasindice_inc_exc_edicao x
					where	a.cd_laboratorio 	= x.cd_laboratorio
					and     a.cd_apresentacao	= x.cd_apresentacao
					and    	a.cd_medicamento	= x.cd_medicamento
					and	x.dt_atualizacao	<= clock_timestamp());

return	ie_situacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
