CREATE OR REPLACE FUNCTION obter_busca_padrao_suep ( ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE

	
ie_formato_padrao_w		varchar(5) := 'A';


/*
Programas de saúde = PS
Agendamentos = AG
Lista de pendencias = LP
Loco regional = LR
Lista de problemas = LI
Partograma = PA
Metas de cuidado = MC
Eventos = ET
Medicamentos em uso = MU
Ganhos e perdas = PG
Saude da mulher = SM
CPOE = CP
Historia do parto = HP
Nascimento = NS
Pré-natal = PN
Amamentação = AM
Vacinas = VA
Lista de problemas árvore = LA
Atendimentos = AT
Diagnóstico de enfermagem = DE
Regulação = RG
Alergias = AL
*/
	

BEGIN

if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then

	if (ie_tipo_item_p in ('AL','PS','AG','LP','LR','LI','MU','SM','VA','LA','AT', 'RG')) then
	
		ie_formato_padrao_w := 'P';
		
	elsif (ie_tipo_item_p in ('PA','MC','ET','PG','CP','HP','NS','PN','AM','DE')) then
	
		ie_formato_padrao_w := 'A';
	
	end if;

end if;

return	ie_formato_padrao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
