SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cadastro_sql ( nm_tabela_p text, nm_atributo_p text) RETURNS varchar AS $body$
DECLARE


ds_cadastro_w		varchar(80);


BEGIN

select	d.ds_cadastro
into STRICT	ds_cadastro_w
from	tabela_sistema d
where	nm_tabela = (
	SELECT	max(b.nm_tabela_referencia)
	from	integridade_atributo	c,
		integridade_referencial	b,
		tabela_atributo		a
	where	a.nm_tabela			= nm_tabela_p
	and 	a.nm_atributo			= nm_atributo_p
	and	a.nm_tabela			= b.nm_tabela
	and	b.nm_tabela			= c.nm_tabela
	and	b.nm_integridade_referencial	= c.nm_integridade_referencial
	and	c.nm_atributo			= nm_atributo_p);

return	ds_cadastro_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
