CREATE OR REPLACE FUNCTION obter_caixa_padrao_panorama ( cd_estab_p usuario_caixa.cd_estabelecimento%TYPE, nm_usuario_p usuario_caixa.nm_usuario%TYPE ) RETURNS bigint AS $body$
DECLARE

    nr_caixa_padrao_w usuario_caixa.nr_seq_caixa%TYPE;

BEGIN
    SELECT
        coalesce(max(nr_seq_caixa), null) caixa
    INTO STRICT nr_caixa_padrao_w
    FROM
        usuario_caixa
    WHERE
        nm_usuario_param = nm_usuario_p
        and cd_estabelecimento = cd_estab_p;

    IF coalesce(nr_caixa_padrao_w::text, '') = '' THEN        
        SELECT 
            coalesce(max(a.nr_seq_caixa), null) caixa
        INTO STRICT nr_caixa_padrao_w
        FROM 
            CAIXA_SALDO_DIARIO a, caixa b
        WHERE 
        a.nr_seq_caixa = b.nr_sequencia
        and b.cd_Estabelecimento = cd_estab_p
        and b.ie_situacao = 'A'
        and coalesce(a.dt_fechamento::text, '') = ''
        order by dt_saldo desc;
    END IF;

    RETURN nr_caixa_padrao_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
