CREATE OR REPLACE FUNCTION obter_calculo_etap_int_sol_md ( ie_operacao_p text, qt_operacao_p text, ie_continuo_p text, qt_tempo_infusao_p bigint, qt_volume_sol_p bigint default null, qt_vel_infusao_p bigint default null) RETURNS bigint AS $body$
DECLARE


qt_hora_fase_w bigint;

BEGIN
	if (ie_operacao_p = 'X') then
		qt_hora_fase_w	:= dividir_md(24,coalesce(qt_operacao_p, 0));
	elsif (ie_continuo_p = 'S') and (ie_operacao_p = 'H') and (qt_volume_sol_p IS NOT NULL AND qt_volume_sol_p::text <> '') and (qt_volume_sol_p > 0) and (qt_vel_infusao_p IS NOT NULL AND qt_vel_infusao_p::text <> '') and (qt_vel_infusao_p > 0) then
		qt_hora_fase_w	:= dividir_md(qt_volume_sol_p,qt_vel_infusao_p);
	elsif (ie_operacao_p = 'H') then
		qt_hora_fase_w	:= qt_operacao_p;
	elsif (ie_operacao_p in ('F','V')) then
		qt_hora_fase_w	:= dividir_md(qt_tempo_infusao_p,coalesce(qt_operacao_p, 0));
	else
		qt_hora_fase_w	:= dividir_md(qt_tempo_infusao_p,coalesce(qt_operacao_p, 0));
	end if;

	if (qt_hora_fase_w > qt_tempo_infusao_p) then
		qt_hora_fase_w	:= qt_tempo_infusao_p;
	end if;

    return qt_hora_fase_w;
end;
$body$
