SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_campos_cheque_cr (nr_seq_cheque_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_campos_w		varchar(255);
nr_cheque_w		varchar(254);
ds_banco_w		varchar(254);
cd_agencia_w	varchar(254);
nr_conta_w		varchar(254);
ds_portador_w	varchar(254);


BEGIN 
 
 
select	a.nr_cheque, 
	b.ds_banco, 
	a.cd_agencia_bancaria, 
	a.nr_conta, 
	obter_nome_pf_pj(a.cd_pessoa_fisica, a.cd_cgc) ds_portador 
into STRICT	nr_cheque_w, 
	ds_banco_w, 
	cd_agencia_w, 
	nr_conta_w, 
	ds_portador_w 
from	cheque_cr a, 
	banco b 
where	a.cd_banco		= b.cd_banco 
and	a.nr_seq_cheque		= nr_seq_cheque_p;
 
 
if (nr_cheque_w IS NOT NULL AND nr_cheque_w::text <> '') then 
	ds_campos_w	:= ds_campos_w || 'Nr:' || nr_cheque_w;	
end if;
 
 
if (ds_banco_w IS NOT NULL AND ds_banco_w::text <> '') then 
	ds_campos_w	:= ds_campos_w || ' - Bco: ' || ds_banco_w;	
end if;
 
if (cd_agencia_w IS NOT NULL AND cd_agencia_w::text <> '') then 
	ds_campos_w	:= ds_campos_w || ' - Ag:' || cd_agencia_w;	
end if;
 
if (nr_conta_w IS NOT NULL AND nr_conta_w::text <> '') then 
	ds_campos_w	:= ds_campos_w || ' -'||obter_desc_expressao(502127)/*'Conta:'*/ || nr_conta_w;	
end if;
 
if (ds_portador_w IS NOT NULL AND ds_portador_w::text <> '') then 
	ds_campos_w	:= ds_campos_w || ' - ' || ds_portador_w;	
end if;
 
 
return	ds_campos_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_campos_cheque_cr (nr_seq_cheque_p bigint) FROM PUBLIC;

