CREATE OR REPLACE FUNCTION obter_campo_disp_tipo_doc (nr_seq_tipo_doc_p bigint, ie_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_p		varchar(1) := 'N';
ie_cart_convenio_w	tipo_documentacao.ie_cart_convenio%type;


BEGIN

select	coalesce(ie_cart_convenio,'X')
into STRICT	ie_cart_convenio_w
from	tipo_documentacao
where	nr_sequencia = nr_seq_tipo_doc_p;

if (coalesce(ie_cart_convenio_w, 'X') = 'S') then
	begin
	if (coalesce(ie_campo_p, 'X') in ('1','2','3','4','5','6','7','8')) then
		ds_retorno_p := 'S';
	end if;
	end;
end if;

return	ds_retorno_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
