CREATE OR REPLACE FUNCTION obter_campo_exame_regra_cpoe ( nr_seq_proc_interno_p bigint, cd_setor_atendimento_p bigint, ie_campo_p text) RETURNS varchar AS $body$
DECLARE

					
ds_retorno_w					varchar(100);
ie_administracao_w				varchar(1);
ie_duracao_w					varchar(1);
si_rad_interpretation_w			varchar(1);

c01 CURSOR FOR
SELECT	max(ie_administracao), max(ie_duracao), max(si_rad_interpretation)	
from	exame_regra_cpoe
where	nr_seq_proc_interno = nr_seq_proc_interno_p
and		coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
order 	by	coalesce(cd_setor_atendimento,0);


BEGIN

if (pkg_i18n.get_user_locale() = 'ja_JP') then

	open c01;
	loop
	fetch c01 into ie_administracao_w, ie_duracao_w, si_rad_interpretation_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (ie_campo_p = 'D') then
				ds_retorno_w := ie_duracao_w;
			elsif (ie_campo_p = 'R') then
				ds_retorno_w := si_rad_interpretation_w;
			elsif (ie_campo_p = 'A') then
				ds_retorno_w := ie_administracao_w;
			end if;
		end;
	end loop;
	close c01;

end if;

return ds_retorno_w;

end;
$body$
