CREATE OR REPLACE FUNCTION obter_campo_lista_problema ( ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

			
ds_tipo_w		varchar(255) := '';
cd_expressao_w	bigint;
			

BEGIN

if (ie_tipo_p IS NOT NULL AND ie_tipo_p::text <> '') then

	If (ie_tipo_p = 'PROP') then
	
		cd_expressao_w := 296350;
	
	elsif (ie_tipo_p = 'DURA') then
	
		cd_expressao_w := 289003;
	
	elsif (ie_tipo_p = 'DATI') then
	
		cd_expressao_w := 286964;
		
	elsif (ie_tipo_p = 'INTE') then
	
		cd_expressao_w := 292143;
		
	elsif (ie_tipo_p = 'STAT') then
	
		cd_expressao_w := 298824;
		
	elsif (ie_tipo_p = 'COMP') then
	
		cd_expressao_w := 285555;
		
	elsif (ie_tipo_p = 'CUID') then
	
		cd_expressao_w := 788654;

	elsif (ie_tipo_p = 'DAFI') then
	
		cd_expressao_w := 326944;	
	end if;	
	
	Select 	max(obter_desc_expressao(cd_expressao_w))
	into STRICT	ds_tipo_w
	;
	
end if;

return ds_tipo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
