CREATE OR REPLACE FUNCTION obter_campo_obrigatorio_sc ( cd_estabelecimento_p bigint, ie_tipo_servico_p text, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE

/*
F	Obrigar fornecedor (PJ ou PF)
O	Obrigar observação
V	Obrigar vencimento
NF	Obrigar nota fiscal
B	Obrigar banco
AG	Obrigar agência
CC	Obrigar conta corrente
DC	Obrigar dígito conta
*/
ie_fornecedor_w			regra_campo_obrig_sc.ie_fornecedor%type := 'N';
ie_observacao_w			regra_campo_obrig_sc.ie_observacao%type := 'N';
ie_vencimento_w			regra_campo_obrig_sc.ie_vencimento%type := 'N';
ie_nota_fiscal_w			regra_campo_obrig_sc.ie_nota_fiscal%type := 'N';
ie_banco_w			regra_campo_obrig_sc.ie_banco%type := 'N';
ie_agencia_w			regra_campo_obrig_sc.ie_agencia%type := 'N';
ie_conta_corrente_w		regra_campo_obrig_sc.ie_conta_corrente%type := 'N';
ie_digito_conta_w			regra_campo_obrig_sc.ie_digito_conta%type := 'N';
ds_retorno_w			varchar(1)	:= 'N';

c01 CURSOR FOR
SELECT	coalesce(ie_fornecedor,'N'),
	coalesce(ie_observacao,'N'),
	coalesce(ie_vencimento,'N'),
	coalesce(ie_nota_fiscal,'N'),
	coalesce(ie_banco,'N'),
	coalesce(ie_agencia,'N'),
	coalesce(ie_conta_corrente,'N'),
	coalesce(ie_digito_conta,'N')
from	regra_campo_obrig_sc
where	cd_estabelecimento = cd_estabelecimento_p
and	ie_tipo_servico = ie_tipo_servico_p;



BEGIN

open C01;
loop
fetch C01 into
	ie_fornecedor_w,
	ie_observacao_w,
	ie_vencimento_w,
	ie_nota_fiscal_w,
	ie_banco_w,
	ie_agencia_w,
	ie_conta_corrente_w,
	ie_digito_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_fornecedor_w		:= ie_fornecedor_w;
	ie_observacao_w		:= ie_observacao_w;
	ie_vencimento_w		:= ie_vencimento_w;
	ie_nota_fiscal_w		:= ie_nota_fiscal_w;
	ie_banco_w		:= ie_banco_w;
	ie_agencia_w		:= ie_agencia_w;
	ie_conta_corrente_w	:= ie_conta_corrente_w;
	ie_digito_conta_w		:= ie_digito_conta_w;
	end;
end loop;
close C01;


if (ie_retorno_p = 'F') then
	ds_retorno_w := ie_fornecedor_w;
elsif (ie_retorno_p = 'O') then
	ds_retorno_w := ie_observacao_w;
elsif (ie_retorno_p = 'V') then
	ds_retorno_w := ie_vencimento_w;
elsif (ie_retorno_p = 'NF') then
	ds_retorno_w := ie_nota_fiscal_w;
elsif (ie_retorno_p = 'B') then
	ds_retorno_w := ie_banco_w;
elsif (ie_retorno_p = 'AG') then
	ds_retorno_w := ie_agencia_w;
elsif (ie_retorno_p = 'CC') then
	ds_retorno_w := ie_conta_corrente_w;
elsif (ie_retorno_p = 'DC') then
	ds_retorno_w := ie_digito_conta_w;
end if;

return	ds_retorno_w;

end;
$body$
