SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_campo_uk_unica ( nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


nm_indice_w	varchar(255);
nm_atributo_w	varchar(255);


BEGIN

select 	max(a.nm_indice)
into STRICT	nm_indice_w
from 	INDICE a
where 	a.nm_tabela = nm_tabela_p
and 	a.IE_TIPO = 'UK'
and 	1 = (SELECT count(*) from INDICE_ATRIBUTO x
			where x.NM_INDICE = a.nm_indice);

if (nm_indice_w IS NOT NULL AND nm_indice_w::text <> '') then
	select	max(NM_ATRIBUTO)
	into STRICT	nm_atributo_w
	from	INDICE_ATRIBUTO
	where	nm_indice	= nm_indice_w;
end if;


return	nm_atributo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_campo_uk_unica ( nm_tabela_p text) FROM PUBLIC;

