CREATE OR REPLACE FUNCTION obter_capacidade_barthel (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);


BEGIN

select 	CASE WHEN IE_CAPACIDADE=0 THEN 'Dependente' WHEN IE_CAPACIDADE=5 THEN 'Precisa de ajuda, mas consegue vestir a metade sem ser ajudado' WHEN IE_CAPACIDADE=10 THEN 'Independente: incluindo botões; zíperes; laços; etc.' END
into STRICT	ds_retorno_w
from	escala_barthel
where	nr_sequencia = nr_sequencia_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
