CREATE OR REPLACE FUNCTION obter_capa_ficha_financ_lib (nr_seq_capa_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_liberado_w		varchar(1);

C01 CURSOR FOR
	SELECT	ie_liberado
	from	capa_ficha_financ_lib b
	where	b.nr_seq_capa				= nr_seq_capa_p
	and	coalesce(nm_usuario_lib, nm_usuario_p)	= nm_usuario_p
	and	coalesce(cd_perfil, cd_perfil_p)		= cd_perfil_p
	order by nm_usuario_lib desc, cd_perfil desc;

BEGIN

open C01;
loop
fetch C01 into
	ie_liberado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w :=	ie_liberado_w;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
