CREATE OR REPLACE FUNCTION obter_capitulo_ciap (nr_seq_ciap_p bigint, ie_opcao_p text default 'D') RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_capitulo_w		varchar(150);
cd_capitulo_w		varchar(10);



BEGIN

if (nr_seq_ciap_p IS NOT NULL AND nr_seq_ciap_p::text <> '') then

		select	max(c.ds_capitulo),
				max(c.cd_capitulo)
		into STRICT	ds_capitulo_w,
				cd_capitulo_w
		from	problema_ciap a,
			componente_ciap b,
			capitulo_ciap c
		where	a.nr_seq_componente	=	b.nr_sequencia
		and	b.nr_seq_capitulo	=	c.nr_sequencia
		and	a.nr_sequencia		=	nr_seq_ciap_p;

	if (ie_opcao_p = 'C') then
		ds_retorno_w :=  cd_capitulo_w;
	else
		ds_retorno_w :=  ds_capitulo_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
