CREATE OR REPLACE FUNCTION obter_caption_escala (ie_escala_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(254);			


BEGIN

SELECT max(substr(coalesce(obter_desc_expressao(CD_EXP_CAPTION), ds_caption),1,60))
into STRICT	ds_retorno_w
FROM	escala_documentacao ed
WHERE	ie_escala = ie_escala_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
