CREATE OR REPLACE FUNCTION obter_carater_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(10);
			

BEGIN 
 
ds_retorno_w := 0;
 
 
if (nr_atendimento_p > 0) then 
 
