CREATE OR REPLACE FUNCTION obter_cargo_pf (cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*ie_opcao_p
C - Código
D - Descrição	*/
cd_cargo_w	bigint;
ds_cargo_w	varchar(100);


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	begin
	select	max(b.cd_cargo),
		max(b.ds_cargo)
	into STRICT	cd_cargo_w,
		ds_cargo_w
	from	pessoa_fisica a,
		cargo b
	where	a.cd_cargo = b.cd_cargo
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (ie_opcao_p = 'C') then
		return cd_cargo_w;
	elsif (ie_opcao_p = 'D') then
		return ds_cargo_w;
	end if;
	end;
else
	return '';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
